function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
var ga=new GlideAjax('lmsAjax');
ga.addParam('sysparm_name','getDateDiff');
ga.addParam('sysparm_start',g_form.getValue('start_date'));
ga.addParam('sysparm_end',g_form.getValue('end_date'));
ga.getXML(ajaxprocesador);
   //Type appropriate comment here, and begin script below

}

function ajaxprocesador(response){

   var answer =response.responseXML.documentElement.getAttribute('answer');

   g_form.setValue('duration',answer/(1000*60*60*24));
}
