sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
   var ga = new GlideAjax('lmsAjax');
   ga.addParam('sysparm_name', 'getLeaveBucket');
   ga.addParam('sysparm_user', g_user.userID);
   ga.addParam('sysparm_leavetype', newValue);
   ga.getXML(ajaxprocesador);
   //Type appropriate comment here, and begin script below

}

function ajaxprocesador(response) {
debugger;
   var answer = response.responseXML.documentElement.getAttribute('answer');
   debugger;
   var result = JSON.parse(answer);

   g_form.setValue('accrued', result?.accrued?result.accrued:'');
   g_form.setValue('balance', result?.balance?result.balance:'');
   g_form.setValue('taken', result?.taken?result.taken:'');
}
