sys_script_include.script:
var lmsAjax = Class.create();
lmsAjax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getDateDiff: function () {
        var d1 = new GlideDate();
        d1.setDisplayValue(this.getParameter('sysparm_start'));

        var d2 = new GlideDate();
        d2.setDisplayValue(this.getParameter('sysparm_end'));
        var duration = GlideDate.subtract(d1, d2);
        return duration.getNumericValue();
    },
    getLeaveBucket: function () {

        gs.info('sysparm_user:' +this.getParameter('sysparm_user'));
        gs.info('sysparm_leavetype:' +this.getParameter('sysparm_leavetype'));
        var record = new GlideRecord('x_652573_ims_leave_bucktet');
        record.addQuery('employee', this.getParameter('sysparm_user'));
        record.addQuery('leave_type', this.getParameter('sysparm_leavetype'));
        record.query();
        if (record.next()) {
            gs.info("entro");
            var leaveDetails = {};
            leaveDetails.accrued = '' + record.accrued;
            leaveDetails.balance = '' + record.balance;
            leaveDetails.taken = '' + record.taken;
            return JSON.stringify(leaveDetails);

        }
    },
    type: 'lmsAjax'
});
